mapper.select:
select job_stop.ids, stop_id, job_stop.job_id, title as job_title, created_user_id, create_user.real_name as created_user_name, job_stop.created_time, refund, content, job_stop.status, read_time, process_time, process_user_id, process_user.real_name as process_user_name, process_remark, process_read_time from job_stop left join job on job_stop.job_id=job.job_id left join user_info create_user on created_user_id=user_id left join user_info process_user on process_user_id=process_user.user_id where 1=1
and job_stop.job_id=#{jobId}
and created_user_id=#{createdUserId}
and process_user_id=#{processUserId}
and read_time is null
and created_user_id<>#{userId}
and process_read_time is null
and process_user_id<>#{userId}
order by ids desc
limit #{offset}, #{size}
