mapper.select:
select task.ids, task.task_id, task.title, task.days, task.price, created_user_id, create_user.real_name as created_user_name, task.created_time, pid, task.code, job_id, job.title as job_title, job.partyb_id, partyb.real_name as partyb_name from task left join user_info create_user on created_user_id = user_id left join job on job.task_id = task.task_id left join user_info partyb on partyb_id = partyb.user_id where 1 = 1
and task.title like '%'+#{titleKey}+'%'
and pid=#{pid}
and pid is null
and created_user_id=#{createdUserId}
order by ids desc
limit #{offset},#{size}

mapper.update:
update task


        title=#{title},


        days=#{days},


        price=#{price},


        code=#{code}


where task_id=#{taskId}

update task_detail set
detail=#{detail}
where task_id=#{taskId}

mapper.select:
select count(*) as total from task left join user_info create_user on created_user_id = user_id left join job on job.task_id = task.task_id left join user_info partyb on partyb_id = partyb.user_id where 1 = 1
and task.title like '%'+#{titleKey}+'%'
and pid=#{pid}
and pid is null
and created_user_id=#{createdUserId}
